def add_age_group(apps, schema_editor):
    Person = apps.get_model("main_app", "Person")

    all_people = Person.objects.all()

    for person in all_people:
        age = person.age

        if age <= 12:
            person.age_group = "Child"
        elif 13 < age < 17:
            person.age_group = "Teen"
        else:
            person.age_group = "Adult"

        person.save()


